import './ExpenseForm.css';
import { useState } from 'react';

const ExpenseForm = () =>{
    const [newTitle,setNewTitle] = useState('');
    const [newAmount,setNewAmount] = useState('');
    const [newDate,setNewDate] = useState('');

    // const [userInput,setUserInput] = useState({
    //     newTitle:'',
    //     newAmount:'',
    //     newDate:''
    // });

    // const titleChangeHandler = (event) => {
    //     setNewTitle(event.target.value);
    //     // setUserInput({
    //     //     ...userInput,
    //     //     newTitle:event.target.value
    //     // });
    //     // setUserInput((prevState) => {
    //     //     return {...prevState,newTitle: event.target.value};
    //     // });
    // }
    // const amountChangeHandler = (event) => {
    //     setNewAmount(event.target.value);
    // //    setUserInput({
    // //     ...userInput,
    // //     newAmount:event.target.value
    // // });
    // }
    // const dateChangeHandler = (event) => {
    //     setNewDate(event.target.value);
    //     // setUserInput({
    //     //     ...userInput,
    //     //     newDate:event.target.value
    //     // });
    // }

    const inputChangeHandler = (identifier,value) => {
        if (identifier === 'title'){
            setNewTitle(value)
        }else if (identifier === 'amount'){
            setNewAmount(value)
        }else{
            setNewDate(value)
        }
    }

    return <form>
        <div className='new-expense__controls'>
            <div className='new-expense__control'>
                <label>Title</label>
                <input type='text' onChange={(event) => inputChangeHandler('title',event.target.value)}/>
            </div>
            <div className='new-expense__control'>
                <label>Amount</label>
                <input type='number' min="0.01" step="0.01" onChange={(event) => inputChangeHandler('amount',event.target.value)}/>
            </div>
            <div className='new-expense__control'>
                <label>Date</label>
                <input type='date' min="2019-01-01" max="2022-12-31" onChange={(event) => inputChangeHandler('date',event.target.value)}/>
            </div>
        </div>
        <div className='new-expense__actions'>
            <button type='submit'>Add Expense</button>
        </div>
    </form>
};

export default ExpenseForm;
